actions.html:
{% set labels = [
  "",
  "Strongly Disagree",
  "Disagree",
  "Somewhat Disagree",
  "Undecided",
  "Somewhat Agree",
  "Agree",
  "Strongly Agree"
] %}
{% set colors = [
  '#CF2C1D',
  '#CF2C1D',
  '#DE5714',
  '#EE830C',
  '#FEAF04',
  '#CBAE06',
  '#98AD0B',
  '#66AD11'
] %}
{% for rating in var_ratings %}
<div>
<span style="float:left;display:inline-block;background-color:{{colors[rating.custom_{{{uid.field_83}}}]}};padding:5px 10px;text-align:center;font-weight:bold;color:white;border-radius:15px;margin:0px 5px 5px 0px;text-shadow:0px 0px 5px #666666;">{{labels[rating.custom_{{{uid.field_83}}}]}}</span>
{% if rating.custom_{{{uid.field_91}}} %}
<i>"{{rating.custom_{{{uid.field_91}}}}}"</i> - 
{% endif %}
<a href="javascript:;" class="cerb-peek-trigger no-underline" data-context="{{rating._context}}" data-context-id="{{rating.id}}"><b>{{rating.subject}}</b></a>
<small style="color:gray;">{{rating.custom_{{{uid.field_80}}}|date_pretty}}</small>
</div>
<br style="clear:both;">
{% endfor %}
<a href="javascript:;" class="cerb-search-trigger" data-context="cerberusweb.contexts.ticket" data-query="customerEffort.rating:!null">see more...</a>

<script type="text/javascript">
$(function() {
  var $widget = $('#workspaceWidget{{widget_id}}');
  $widget.find('.cerb-peek-trigger').cerbPeekTrigger();
  $widget.find('.cerb-search-trigger').cerbSearchTrigger();
});
</script>

