actions.jquery_script:
var $reply = $(this);
var $form = {
	fields: $reply.find('form:nth(0)'),
	actions: $reply.find('form:nth(1)')
};

var $textarea_reply = $form.actions.find('textarea[name=content]');
var txt = $textarea_reply.val();

if(-1 == txt.indexOf('#survey-csat')) {
  var find = "#signature";
  var pos = txt.indexOf(find);
  
  $textarea_reply.setCursorLocation(pos + find.length);
  $textarea_reply.insertAtCursor("\n#survey-csat");
  $textarea_reply.setCursorLocation(0);
}

